INSERT INTO `b_search_content_text` VALUES (1,'b907fdd4c88f02f324d8a003b9911b65','DENTOPTICS\r\nCAD/СAM\nСИСТЕМЫ\rМЫ ПРЕДЛАГАЕМ ИНТРАОРАЛЬНЫЕ СКАНЕРЫ, ФРЕЗЕРНЫЕ СТАНКИ, 3D ПРИНТЕРЫ ВЕДУЩИХ МИРОВЫХ ПРОИЗВОДИТЕЛЕЙ.\rDENTOPTICS DIGITAL РАБОТАЕТ ТОЛЬКО НАПРЯМУЮ С ЗАВОДАМИ - ПРОИЗВОДИТЕЛЯМИ, ИМЕЯ ПРЯМЫЕ КОНТРАКТЫ, А ЗНАЧИТ - ЛУЧШИЕ ЦЕНЫ И ОФИЦИАЛЬНЫЙ СЕРВИС.\rНАШИ СПЕЦИАЛИСТЫ ПОДБЕРУТ ОПТИМАЛЬНОЕ РЕШЕНИЕ ДЛЯ ВАШЕЙ ПРАКТИКИ, А В УЧЕБНОМ ЦЕНТРЕ #DENTALWORKSHOP ВЫ СМОЖЕТЕ ПОСМОТРЕТЬ РАЗЛИЧНЫЕ КОМБИНАЦИИ ПРЕДЛАГАЕМОГО ОБОРУДОВАНИЯ В ДЕЙСТВИИ. СДЕЛАЙТЕ ШАГ В ЦИФРОВОЕ БУДУЩЕЕ С НАМИ!\rСКАНЕРЫ \rИНТРАОРАЛЬНЫЕ\rТРАДИЦИОННЫЕ МЕТОДЫ ДИАГНОСТИКИ И МОДЕЛИРОВАНИЯ ЗУБОВ УСТУПАЮТ МЕСТО НОВЫМ, ОСНОВАННЫМ НА 3D-СКАНИРОВАНИИ И 3D-ПЕЧАТИ.\nСКАНЕРЫ \rЛАБОРАТОРНЫЕ\rЭВОЛЮЦИЯ ОТ АНАЛОГОВОЙ ДО ЦИФРОВОЙ СТОМАТОЛОГИИ УЖЕ НЕ ЯВЛЯЕТСЯ ДЕЛОМ ДАЛЕКОГО БУДУЩЕГО, А СОВРЕМЕННОЙ РЕАЛЬНОСТЬЮ.\nФРЕЗЕРНЫЕ \rСТАНКИ\rЭВОЛЮЦИЯ ОТ АНАЛОГОВОЙ ДО ЦИФРОВОЙ СТОМАТОЛОГИИ УЖЕ НЕ ЯВЛЯЕТСЯ ДЕЛОМ ДАЛЕКОГО БУДУЩЕГО, А СОВРЕМЕННОЙ РЕАЛЬНОСТЬЮ.\nCAD/CAM \rКОМПЛЕКТЫ\rЭВОЛЮЦИЯ ОТ АНАЛОГОВОЙ ДО ЦИФРОВОЙ СТОМАТОЛОГИИ УЖЕ НЕ ЯВЛЯЕТСЯ ДЕЛОМ ДАЛЕКОГО БУДУЩЕГО, А СОВРЕМЕННОЙ РЕАЛЬНОСТЬЮ.\n#DENTALWORKSHOP\rАТМОСФЕРА ЭНЕРГИИ И ЭРГОНОМИКИ, УНИКАЛЬНОЕ ОСНАЩЕНИЕ КЛАССА И ВНИМАТЕЛЬНОЕ ОТНОШЕНИЕ К МЕЛОЧАМ – ЭТО ПРОСТРАНСТВО #DENTALWORKSHOP.\rСМОТРЕТЬ\rРАСХОДНЫЕ МАТЕРИАЛЫ\rIPS E.MAX ZIRCAD MT BL 98.5-14/1\rСРЕДНЯЯ ПРОЗРАЧНОСТЬ\rКЛАСС МАТЕРИАЛА\rПРОЗРАЧНОСТЬ 4Y-TZP | 45%\rЦВЕТА 8\r15 090 ₽\rПОДРОБНЕЕ\rВ КОРЗИНУ\rIPS E.MAX ZIRCAD MT BL 98.5-14/1\rСРЕДНЯЯ ПРОЗРАЧНОСТЬ\rКЛАСС МАТЕРИАЛА\rПРОЗРАЧНОСТЬ 4Y-TZP | 45%\rЦВЕТА 8\r15 090 ₽\rПОДРОБНЕЕ\rВ КОРЗИНУ\rIPS E.MAX ZIRCAD MT BL 98.5-14/1\rСРЕДНЯЯ ПРОЗРАЧНОСТЬ\rКЛАСС МАТЕРИАЛА\rПРОЗРАЧНОСТЬ 4Y-TZP | 45%\rЦВЕТА 8\r15 090 ₽\rПОДРОБНЕЕ\rВ КОРЗИНУ\rIPS E.MAX ZIRCAD MT BL 98.5-14/1\rСРЕДНЯЯ ПРОЗРАЧНОСТЬ\rКЛАСС МАТЕРИАЛА\rПРОЗРАЧНОСТЬ 4Y-TZP | 45%\rЦВЕТА 8\r15 090 ₽\rПОДРОБНЕЕ\rВ КОРЗИНУ\rIPS E.MAX ZIRCAD MT BL 98.5-14/1\rСРЕДНЯЯ ПРОЗРАЧНОСТЬ\rКЛАСС МАТЕРИАЛА\rПРОЗРАЧНОСТЬ 4Y-TZP | 45%\rЦВЕТА 8\r15 090 ₽\rПОДРОБНЕЕ\rВ КОРЗИНУ\rIPS E.MAX ZIRCAD MT BL 98.5-14/1\rСРЕДНЯЯ ПРОЗРАЧНОСТЬ\rКЛАСС МАТЕРИАЛА\rПРОЗРАЧНОСТЬ 4Y-TZP | 45%\rЦВЕТА 8\r15 090 ₽\rПОДРОБНЕЕ\rВ КОРЗИНУ\rОТЗЫВЫ\rМЫ ПРЕДЛАГАЕМ СЕГОДНЯ ТО, ЧТО СТАНЕТ ТРЕНДОМ ЗАВТРА.\rЕЩЕ НЕДАВНО ВСЕ ТОЛЬКО МЕЧТАЛИ И ГОВРИЛИ О СТОМАТОЛОГИИ 21-ГО ВЕКА, СТОМАТОЛОГИИ БУДУЩЕГО, ГДЕ ЦИФРОВЫЕ ТЕХНОЛИИ МЕНЯЮТ ПРИВЫЧНЫЙ ПОДХОД И ПРИВЫЧНЫЕ ПРОТОКОЛЫ. СЕГОДНЯ ВСЕ БОЛЬШЕ И БОЛЬШЕ ВРАЧЕЙ РАБОТАЮТ С ЦИФРОВЫМИ ОТТИСКАМИ, А КОМПЬЮТЕРНЫЕ ПРОГРАММЫ ПОМОГАЮТ СДЕЛАТЬ РЕЗУЛЬТАТ БЫСТРЫМ И ТОЧНЫМ. МЫ ДЕЛАЕМ БОЛЬШУЮ СТАВКУ НА ЦИФРОВУЮ СТОМАТОЛОГИЮ И ПОМОГАЕМ НЕ ТОЛЬКО ВЫБРАТЬ ПОДХОДЯЩУЮ ДЛЯ ВАС ТЕХНОЛОГИЮ, НО И ИНТЕГРИРОВАТЬ ЕЕ В СУЩЕСТВУЮЩИЙ КЛИНИЧЕСКИЙ ПРОЦЕСС.\rСОЗДАВАЯ DENTOPTICS DIGITAL МЫ ХОТЕЛИ ЧТОБЫ ЭТО БЫЛА КОМПАНИЯ, КОТОРАЯ НЕСЕТ НОВЫЕ ИДЕИ И ТЕХНОЛОГИИ, КОТОРАЯ ПОСТЕПЕННО ПЕРЕСТРАИВАЕТ МЫШЛЕНИЕ СТОМАТОЛОГОВ О СВОЕЙ РАБОТЕ, НЕСЕТ УДОВОЛЬСТВИЕ, УСПЕХ И НОВОЕ КАЧЕСТВО ЖИЗНИ И ВРАЧАМ И ПАЦИЕНТАМ.\rУДОБНАЯ ОПЛАТА ОНЛАЙН\rНА НАШЕМ САЙТЕ УДОБНАЯ ОПЛАТА КАРТАМИ MASTERCARD, VISA, МИР\rДОСТАВКА ПО ВСЕЙ РОССИИ\rВРЕМЯ - ЦЕННЫЙ РЕСУРС, ИМЕННО ПОЭТОМУ МЫ ОРГАНИЗОВАЛИ БЫСТРУЮ И НАДЕЖНУЮ ДОСТАВКУ\rСАМОЕ ИННОВАЦИОННОЕ ОБОРУДОВАНИЕ\rПОКУПАЯ ИННОВАЦИОННОЕ ОБОРУДОВАНИЕ, ВЫ ДЕЛАЕТЕ ЦЕННУЮ ИНВЕСТИЦИЮ В СЕБЯ И СВОЮ ПРАКТИКУ\rПОЛНАЯ ДОКУМЕНТАЦИЯ\rИНСТРУКЦИИ, РЕГИСТРАЦИОННЫЕ УДОСТОВЕРЕНИЯ, ДЕКЛАРАЦИИ СООТВЕТСТВИЯ, А ТАКЖЕ ГАРАНТИЙНЫЙ ТАЛОН ВЫДАЮТСЯ КАЖДОМУ КЛИЕНТУ.\rВЫГОДНЫЕ УСЛОВИЯ\rНАШИ МЕНЕДЖЕРЫ ГОТОВЫ ПРЕДОСТАВИТЬ ВЫГОДНЫЕ УСЛОВИЯ ПОКУПКИ. МЫ, ЦЕНИМ НАШЕГО ПОКУПАТЕЛЯ!\rШИРОКИЙ ВЫБОР\rОГРОМНЫЙ ВЫБОР КОМПЛЕКТУЮЩИХ ТОВАРОВ, ГОТОВЫХ УСТАНОВОК, CAD/CAM-СИСТЕМ.\rКОМПАНИИ ПРОИЗВОДИТЕЛИ\r\n'),(4,'b28ad424400cabd8184d7d000dff3bd6','КАТАЛОГ ПРОДУКЦИИ\r\n\r\n'),(5,'b28ad424400cabd8184d7d000dff3bd6','КАТАЛОГ ПРОДУКЦИИ\r\n\r\n'),(6,'59776800303fdc620751a333ce41ceea','ЛИЧНЫЙ КАБИНЕТ\r\n\r\n'),(8,'150d379b9d8dbec84f4223193ea1c643','ОПЕРАЦИОННЫЙ МИКРОСКОП\r\nКУРС, КОТОРЫЙ ДАЁТ​ НЕОБХОДИМУЮ​ БАЗУ​ ЗНАНИЙ ДЛЯ ЛЮБОГО СПЕЦИАЛИСТА, РАБОТАЮЩЕГО С УВЕЛИЧЕНИЕМ.\r\n\rИНВЕСТИЦИИ